Ext.define('Actor.view.ViewedWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.viewedwindow',

    onRecentViewedItemClick: function(dataview, record, item, index, e, eOpts) {
        var mainCtr = getController('Main');
        var viewer = Ext.getCmp('viewPanel').down('#viewer');
        if(viewer){
            if(viewer.bdIdx == record.get('bd_idx')){
                if(!Ext.getCmp('eastPanel').getCollapsed()){
                    Ext.getCmp('eastPanel').setCollapsed(true);
                    return;
                }
                else {
                    Ext.getCmp('eastPanel').setCollapsed(false);
                }
            }
            else{
                Ext.getCmp('eastPanel').setCollapsed(false);
            }
        }
        else{
            Ext.getCmp('eastPanel').setCollapsed(false);
        }
        getController('Main').viewDocument(record, '', 'Information', index, true);
    }

});
